import React, { useEffect, useState } from 'react'
import './style.css'

const Temp = () => {

  const [search, setSearch] = useState("Pune");

  const getWeatherInfo = async () => {
    try {
      let url = `https://api.openweathermap.org/data/2.5/weather?q=${search}&appid=68e77a4836eb61ea851985dd81ec94c6`;

      const res = await fetch(url);
      const data = await res.json();
    } catch (error) {
      console.log(error);
    }
  }
  useEffect(() => {
    getWeatherInfo();
  }, [])
  return (
    <>
      {/* Search  */}

      <div className="wrap">
        <div className="search">
          <input type="search" placeholder='search...' autoFocus id='search' className='searchTerm' value={search} onChange={(e) => setSearch(e.target.value)} />
          <button className='searchButton' type='button' onClick={getWeatherInfo}>Search</button>
        </div>
      </div>

      {/* main section */}
      <article className='widget'>
        <div className="weatherIcon">
          <i className={'wi wi-day-sunny'}></i>
        </div>
        <div className="weatherInfo">
          <div className="temperature">
            <span>25.5&deg;</span>
          </div>

          <div className="description">
            <div className="weatherCondition">Sunny</div>
            <div className="place">Pune, India</div>
          </div>
        </div>
        <div className="date">
          {new Date().toLocaleString()}
        </div>

        {/* 4 columns */}
        <div className="extra-temp">
          <div className="temp-info-minmax">
            <div className="two-sided-section">
              <p><i className={"wi wi-sunset"}></i></p>
              <p className='extra-info-leftside'>09:09 PM <br />Sunset</p>
            </div>

            <div className="two-sided-section">
              <p><i className={"wi wi-humidity"}></i></p>
              <p className='extra-info-leftside'>09:09 PM <br />Humidity</p>
            </div>
          </div>

          <div className="weather-extra-info">
            <div className="two-sided-section">
              <p><i className={"wi wi-rain"}></i></p>
              <p className='extra-info-leftside'>09:09 PM <br />Presure</p>
            </div>

            <div className="two-sided-section">
              <p><i className={"wi wi-strong-wind"}></i></p>
              <p className='extra-info-leftside'>09:09 PM <br />Speed</p>
            </div>
          </div>
        </div>
      </article>
    </>
  )
}

export default Temp
